$(function(){$("pre").append("<i class='lw-copy'>copy</i>");$(".lw-copy").click(function(event){var text=$(this).parent().text();var dom=document.createElement("textarea");text=text.substr(0,text.length-4);dom.value=text.trim();dom.setAttribute("style","display: block;width: 1px;height: 1px;");document.body.appendChild(dom);dom.select();var result=document.execCommand("copy");document.body.removeChild(dom);if(result){console.log(result);alert("复制成功")}else{alert("复制失败，请重试")}})});